mapper.select:

select 

from er_event_point
where account_event_id = #{accountEventId,jdbcType=VARCHAR}
  and event_ponit_order = #{eventPonitOrder,jdbcType=INTEGER}
  and event_id = #{eventId,jdbcType=VARCHAR}

mapper.delete:

delete from er_event_point
where account_event_id = #{accountEventId,jdbcType=VARCHAR}
  and event_ponit_order = #{eventPonitOrder,jdbcType=INTEGER}
  and event_id = #{eventId,jdbcType=VARCHAR}

mapper.update:

update er_event_point


    event_point_content = #{eventPointContent,jdbcType=VARCHAR},


    event_point_begin = #{eventPointBegin,jdbcType=VARCHAR},


    event_point_end = #{eventPointEnd,jdbcType=VARCHAR},


where account_event_id = #{accountEventId,jdbcType=VARCHAR}
  and event_ponit_order = #{eventPonitOrder,jdbcType=INTEGER}
  and event_id = #{eventId,jdbcType=VARCHAR}


update er_event_point
set event_point_content = #{eventPointContent,jdbcType=VARCHAR},
  event_point_begin = #{eventPointBegin,jdbcType=VARCHAR},
  event_point_end = #{eventPointEnd,jdbcType=VARCHAR}
where account_event_id = #{accountEventId,jdbcType=VARCHAR}
  and event_ponit_order = #{eventPonitOrder,jdbcType=INTEGER}
  and event_id = #{eventId,jdbcType=VARCHAR}
